function bubbleSort (vals) {
    var temp;
    var len = vals.length;
    for (var i = 0; i < len - 1; i++) {
        for (var k = 0; k < len - i; k++) {
            if (vals[k] > vals[k+1]) {
                temp = vals[k];
                vals[k] = vals[k+1];
                vals[k+1] = temp;
            }
        }
    }
    return vals;
}
